sp_widget.client_script:
function($scope,spModal) {
  /* widget controller */
  var c = this;





	$scope.add=function(){		
		spModal.open({
                    title:'Add new Activity',
                    size:'lg',
                    widget:'widget-form',
                    widgetInput:{table:'x_315700_lms_activitycmp',sys_id:'-1'}
                }).then(function(){
                    console.log('..... closed ......');
                });
	}
		$scope.list=function(){
		var shared={};	
		spModal.open({
                    title:'Activities List',
                    size:'lg',
                    widget:'activitieslist',
			              shared: shared
                }).then(function(){
			  c.data.selectedValue = shared.selection;

                    console.log('..... closed ......');
                });
	}






}
